import React from "react";
import Paginacion from "./Paginacion";
import { CardPokemon } from "../Componentes/CardPokemon";


const pokemon = (props) => {
    const {pokemon, pagina, setPagina, total, loading} = props;

    const lastPage= () => {
        const NextPage = Math.max(pagina - 1, 0);
        setPagina(NextPage);
    };

    const NextPage = () =>{
      const NextPage = Math.min(pagina + 1, total - 1);
      setPagina(NextPage);
    };


  return (
    <div>{loading ? (
        <div className="cargando">
            Cargando pokemones......
        </div>
    ) :(
        <div>
            {pokemon.map((pokemon) => {
                return <CardPokemon pokemon={pokemon} key={pokemon.name}/>;
            })}
            <div className="cargado2"><Paginacion
            pagina={pagina + 1}
            totalPages={total}
            onLeftClick={lastPage}
            onRightClick={NextPage}
            />
            </div>
        </div>
      )}
  </div>
  );
};

export default pokemon;
