import { useEffect, useState } from "react";
import { PokemonContext } from "./PokemonContext";


export const PokemonProvide = ({children}) => {

        //  const allPokemones: never[]

    const [allPokemones,setallPokemones] = useState ([]);
    const [globalpokemones,setGlobalPokemones] = useState([]);
    const[offset, setOffset] = useState(0);

 //------ Llamar los 20 bichitos------//
 
        const getAllPokemons = async(limit =20 ) =>{
          const baseURL = 'https://pokeapi.co/api/v2/'
          const res = await fetch (
            `${baseURL}pokemon?limit=${limit}&offset=${offset}`
            );
            
            const  data = await res.json();
            return data;
 
            //-----Pokemones globales----//
      const getGlobalPokemones = async () => {
        const baseURL = "https://pokeapi.co/"
      }
            
         const trust = data.results.map (async(pokemon) =>{
          const res = await fetch(pokemon.url);
          const data = await res.json();
          return data 
         });
           const results = await Promise.all(trust);
           setGlobalPokemones(results);
           setloading(false)
     }

 //-----Llamar al id-----//
      const savePokemonesById = async () =>{
        const baseURL= "https://pokeapi.co/api/v2";
        const res = await fetch (`${baseURL}pokemon?limit=300&offset=0`);
        const data = await res.json()
        return data
      }

        useEffect(()=>{
         savePokemones();
        },[]);

        useEffect(() =>{
          getGlobalPokemones();
        },[]);
           
   return(
   <PokemonContext.Provider value={{
    valueSearch,
    savePokemones,

    
    }}>
    {children}
   </PokemonContext.Provider>
   );
};
