import React, { useContext } from 'react'
import { PokemonContext } from '../Context/PokemonContext'
import { useLocation } from 'react-router-dom'
import { CardPokemon } from '../Componentes/CardPokemon'

//.......... Que filtre los muñequitos con el nombre ............//
    

    const getPokemonById = async (id) => {
  const baseURL = "https://pokeapi.co/api/v2/"
   const res = await fetch(`${baseURL}pokemon/${id}`)
    const data = await res.json()
    console.log(data)
       return data 
 };


  export const SearchPage = () => {
	const location = useLocation();

	const { globalPokemons } = useContext(PokemonContext);

	const filteredPokemons = globalPokemons.map(pokemon =>
		pokemon.name.includes(location.state.toLowerCase().trim())
	);
	
	const [seacrPokemons, setSearchPokemons] = useState([])
const [loading, setLoading] = useState(true)

   const onSearchSubmit = async(e) =>{
    e.preventDefault()
    const response = await searchPokemons(valueSearch)
    setSearchPokemons(response)
    setLoading(false)
   }

 

	return (
		<div className='container'>
			   <header className='container'>
        <img src={logo} alt="" className='logo' />
     
          
            
                <form onSubmit={onSearchSubmit}>
                <div className='from-group'>
                    <input 
                    type="search" 
                    name='valueSearch'
                    id=''
                    value={valueSearch}
                    onChange={onInputChange}
                    className='input-poke'
                    placeholder='Buscar pokemon'
                    />
                    <button className='btn-search'>Buscar</button>
                </div>
                </form>
      </header>
			<p className='p-search'>
				Se encontraron <span>{filteredPokemons.length}</span>{' '}
				resultados:
			</p>
			<div className='card-list-pokemon container'>
				{filteredPokemons.map(pokemon => (
					<CardPokemon pokemon={pokemon} key={pokemon.id} />
				))}
			</div>
		</div>
	);
};
