        {pagina} = {totalPages}
      </div>
        <div className="icon">
        <button className="boton-Pagi" onClick={onLeftClick}>Adelante</button>

               {allPokemons.map(pokemon =>
                <PokemonList pokemon/>)}
        </div>
    </div>

  );
};

